        var array = Axios.get(Categoriy, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          })
        .then(response =>{
            return response.data
        })
        .catch(error =>{
                console.log(error);
        })
        return array;
    }
    async GetFoods(data){
        var array = Axios.get(FoodsArray, {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          })
        .then(response =>{
            return response.data
        })
        .catch(error =>{
            
        })
        return array;
    }
    async PostRegister(data){
        console.log(data);
        var array = Axios.post(`${Register}`, data)
        .then(response =>{
            return response.data
        })
        .catch(error =>{
            console.log("Неверный формат");
        })
        return array;
    }
    async PostLogin(data){
        console.log(data);
        var array = Axios.post(`${Login}`, data)
        .then(response =>{
            return response.data
        })
        .catch(error =>{
            console.log("Неверный формат");
        })
        return array;
    }
    async PostFoods(id, data){
        var array = Axios.post(`${PostFood}?foodId=${id}`, data, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem("token")}`,
              },
        })
        .then(response =>{
            console.log(response);
            return response.data
        })
        .catch(error =>{
            console.log(error);
        })
        return array;
    }
}

export default new Data()
